"use strict";
require = global.require;
var utils = require('@zoho/lyte-cli-utils'),
	fileManipulation = utils.fileManipulation,
	path = require("path"),
	folders = utils.commonCliUtils,

	filePath;
module.exports = {
	description :"adapters",
	fileMapTokens :function(options)	{
		return {
			__demo__ : options.cliArgs[2]
		}
	},	
	locals :function(options)  {
		return  {
			id : options.cliArgs[2]
		}
	},
	beforeInstall : function(options) {
		if(!folders.checkForAlreadyExistence(options,'adapters')) {
			if(options.d == '.') {
				options.d = undefined;
			}
		} else {
			this.reject(new Error("adapters named "+options.cliArgs[2]+" already registered. Adapter with same name already exists in the src folder of the adapter"));
		}
	},
	beforeUninstall : function(options) {
		var subFolder = (options.d && options.d != '.') ? options.d : '';			
		filePath = path.join(options.folders.src.adapters,subFolder,options.cliArgs[2]+'.js');
		if(!fileManipulation.fileExist(filePath)){
			this.reject(new Error("Adapter named "+path.relative(options.root,filePath)+" doesn't exists"));
		}
	},

	uninstall : function(options) {
		fileManipulation.removeSync(filePath,true);					
		options.log.user({color : 'green', msg : "Removed File : "+path.relative(options.root,filePath)});
		if(fileManipulation.fileExist(options.logPath)) {
			options.log.system("Removed File : "+path.relative(options.root,filePath));
		}
		folders.removeEmptyFolder(filePath,"adapters",options);
	}
}