"use strict";
require = global.require;
var utils = require("@zoho/lyte-cli-utils"),
	fileManipulation = utils.fileManipulation,
	path = require("path"),
	folders = utils.commonCliUtils,
	filePath;
module.exports ={
	description :"mixins",
	fileMapTokens :function(options)	{
		return {
			__demo__ : options.cliArgs[2]
		}
	},
	
	locals :function(options)  {
		return {
			id : options.cliArgs[2]
		}
	},

	beforeInstall : function(options) {
		if(!folders.checkForAlreadyExistence(options,'mixins')) {
			if(options.d == '.') {
				options.d = undefined
			}
		} else {
			this.reject(new Error("Mixins named "+options.cliArgs[2]+" already registered. Mixins with same name already exists in the src folder of the mixins"))
		}		
	},

	beforeUninstall : function(options){
		var subFolder;
		subFolder = (options.d && options.d != '.') ? options.d :''
		
		filePath=path.join(options.folders.src['mixins'],subFolder,options.cliArgs[2]+'.js')	
		if(!fileManipulation.fileExist(filePath)){	
			this.reject(new Error("Mixin named "+filePath+" doesn't exists"))	
		}
	},

	uninstall : function(options) {	
		var permitFlag = true;
	   	fileManipulation.removeSync(filePath,permitFlag);	
		options.log.user({color : 'green', msg : "Removed File : "+path.relative(options.root,filePath)});
		if(fileManipulation.fileExist(options.logPath)) {
			options.log.system("Removed File : "+path.relative(options.root,filePath)+'\n');
		}
		folders.removeEmptyFolder(filePath,"mixins",options);
	}
}
