"use strict";
require = global.require;
var utils = require("@zoho/lyte-cli-utils"),
	fileManipulation = utils.fileManipulation,
	folders = utils.commonCliUtils,
	path = require("path"),
	filePath;
module.exports = {	
	description :"serializers",
	fileMapTokens :function(options)	{
		return {
			__demo__ : options.cliArgs[2]
		}
	},
	
	locals :function(options)  {
		return {
			 id: options.cliArgs[2]
		}
	},

	beforeInstall : function(options) {
		if(!folders.checkForAlreadyExistence(options,'serializers')) {
			if(options.d == '.') {
				options.d = undefined
			}
		} else {
			this.reject(new Error("serializers named "+options.cliArgs[2]+" already registered. Serializers with same name already exists in the src folder of the serializer"))
		}		
	},

	beforeUninstall : function(options){
		var subFolder;
		subFolder = (options.d && options.d != '.') ? options.d :''
		
		
		

		filePath = path.join(options.folders.src['serializers'],subFolder,options.cliArgs[2]+'.js');
		if(!fileManipulation.fileExist(filePath)){
			this.reject(new Error("serializers named "+path.relative(options.root,filePath)+" doesn't exists"))
		}
	},

	uninstall : function(options) {					
	    fileManipulation.removeSync(filePath,true);			
		options.log.user({color : 'green', msg : "Removed File : "+path.relative(options.root,filePath)})
		if(fileManipulation.fileExist(options.logPath)) {
			options.log.system("Removed File : "+path.relative(options.root,filePath)+'\n');
		}
		folders.removeEmptyFolder(filePath,"serializers",options);
	}
}
